hudson.tasks.Shell.command:
echo '
assume_role(){
  local role_arn=${1:-"arn:aws:iam::'${app_acct_id}':role/'${jenkins_provisioning_assume_role_name}'"}
  OUTPUT=$(aws sts assume-role \
      --role-arn $role_arn \
      --role-session-name "teardown-rebuild" \
      --query "Credentials.[AccessKeyId,SecretAccessKey,SessionToken]" \
      --output text)

  export AWS_ACCESS_KEY_ID=$(echo $OUTPUT | awk "{print \$1}")
  export AWS_SECRET_ACCESS_KEY=$(echo $OUTPUT | awk "{print \$2}")
  export AWS_SESSION_TOKEN=$(echo $OUTPUT | awk "{print \$3}")
  export AWS_DEFAULT_REGION=us-east-1
}

reset_role(){
  unset AWS_ACCESS_KEY_ID
  unset AWS_SECRET_ACCESS_KEY
  unset AWS_SESSION_TOKEN
}

export -f assume_role
export -f reset_role
        ' > role_functions.sh

# Provides automation for swapping instances between staging and live target groups, using green/blue deployment strategy.
# Script Usage: # To use this script, specify the names of the staging and live target groups, as well as JSON files # containing tag filters for identifying instances to swap between the target groups.
# Example Usage: # swap_stacks "staging-tg-name" "live-tg-name" "staging-httpd-tags.txt" "live-httpd-tags.txt"
# Function to get Target Group ARN by name
echo ' get_target_group_arn_by_name() { # Use AWS CLI to describe target groups and extract the ARN by name aws elbv2 describe-target-groups \ --query "TargetGroups[?TargetGroupName==\`$1\`].TargetGroupArn" \ --output text }
# Function to get Target Group VPC by name get_target_group_vpc_by_tg_name() { local target_group_name=$1 # Use AWS CLI to describe the target group and extract the VPC ID aws elbv2 describe-target-groups \ --names "$target_group_name" \ --query "TargetGroups[0].VpcId" \ --output text }
# Function to get private IP by tags get_private_ip_by_tags() { local tag_filters_file="$1" # Use AWS CLI to describe instances with specified tag filters from a file and extract private IPs aws ec2 describe-instances \ --filters "file://$tag_filters_file" \ --query "Reservations[].Instances[].PrivateIpAddress" \ --output text }
# Function to check if an EC2 instance is in the same VPC as a target group is_ec2_in_same_vpc_as_target_group() { local ec2_private_ip="$1" local target_group_vpc_id="$2" # Use AWS CLI to describe the EC2 instance and extract its VPC ID local ec2_vpc_id=$(aws ec2 describe-instances \ --filters "Name=private-ip-address,Values=$ec2_private_ip" \ --query "Reservations[0].Instances[0].VpcId" \ --output text)
# Compare the EC2 instances VPC ID with the known target groups VPC ID if [ "$ec2_vpc_id" == "$target_group_vpc_id" ]; then echo "The EC2 instance with private IP $ec2_private_ip is in the same VPC as the target group." return 0 # Return success (0) else echo "The EC2 instance with private IP $ec2_private_ip is NOT in the same VPC as the target group." return 1 # Return failure (non-zero) fi }
# Function to register targets in a target group register_targets() { local local_target_group_vpc="$1" local local_target_group_arn="$2" local httpd_priv_ips=("${@:3}")
for local_httpd_priv_ips in "${httpd_priv_ips[@]}"; do # Check if the EC2 instance is in the same VPC as the target group and register it accordingly if is_ec2_in_same_vpc_as_target_group "$local_httpd_priv_ips" "$local_target_group_vpc"; then aws elbv2 register-targets --target-group-arn $local_target_group_arn --targets "Id=$local_httpd_priv_ips" else aws elbv2 register-targets --target-group-arn $local_target_group_arn --targets "Id=$local_httpd_priv_ips,AvailabilityZone=all" fi done }
# Function to deregister targets from a target group deregister_targets() { local local_target_group_vpc="$1" local local_target_group_arn="$2" local httpd_priv_ips=("${@:3}")
for httpd_priv_ip in "${httpd_priv_ips[@]}"; do # Check if the EC2 instance is in the same VPC as the target group and deregister it accordingly if is_ec2_in_same_vpc_as_target_group "$httpd_priv_ip" "$local_target_group_vpc"; then aws elbv2 deregister-targets --target-group-arn $local_target_group_arn --targets "Id=$httpd_priv_ip" else aws elbv2 deregister-targets --target-group-arn $local_target_group_arn --targets "Id=$httpd_priv_ip,AvailabilityZone=all" fi done }
# Function to wait for a target group to become healthy for all instances wait_for_target_group_health() { local target_group_arn="$1" local wait_for_health="$2" local target_instance_ips=("${@:3}")
local timeout_secs=600 local start_time=$(date +%s)
for target_instance_ip in "${target_instance_ips[@]}"; do local health_status=$(aws elbv2 describe-target-health \ --target-group-arn "$target_group_arn" \ --targets "Id=$target_instance_ip" \ --query "TargetHealthDescriptions[0].TargetHealth.State" \ --output text )
while [[ ${health_status^^} != ${wait_for_health^^} ]]; do local elapsed_time=$(( $(date +%s) - start_time )) if [ $elapsed_time -ge $timeout_secs ]; then echo "Target group health check exceeds timeout" echo "Target=$target_instance_ip Current_Health=${health_status^^} wait_for_health=$wait_for_health" exit 1 fi
# Use AWS CLI to describe the target health status and update the health_status variable health_status=$(aws elbv2 describe-target-health \ --target-group-arn "$target_group_arn" \ --targets "Id=$target_instance_ip" \ --query "TargetHealthDescriptions[0].TargetHealth.State" \ --output text ) echo "$target_instance_ip - ${health_status^^}. Waiting for health of ${wait_for_health^^}" sleep 10 # Wait for 10 seconds before checking again done done }
# Function to swap instances between staging and live target groups swap_stacks() { local staging_tg_name=$1 local live_tg_name=$2 local staging_httpd_tags_file="$3" local live_httpd_tags_file="$4" local fail_empty_stacks="${5,,:-"false",,}" # Set to true for stable prod and release testing echo "staging_tg_name=$1" echo "live_tg_name=$2" echo "staging_httpd_tags_file=$3" echo "live_httpd_tags_file=$4"
# Get Target Group ARNs using the function local local_staging_target_group_arn=$(get_target_group_arn_by_name "$staging_tg_name") local local_live_target_group_arn=$(get_target_group_arn_by_name "$live_tg_name")
# Get target group VPC. Both target groups are known to be in the same VPC local local_target_group_vpc=$(get_target_group_vpc_by_tg_name "$live_tg_name")
# Get private IPs using the function with tags from files local local_staging_httpd_instance_prv_ips=($(get_private_ip_by_tags "$staging_httpd_tags_file")) local local_live_httpd_instance_prv_ips=($(get_private_ip_by_tags "$live_httpd_tags_file"))
# Promote Staging to Live (green to blue) echo "Promote Staging to Live (green to blue)" if [ -z "${local_staging_httpd_instance_prv_ips}" ]; then echo "No staging IP Addresses found when promoting to live server." echo "Ensure nodes have been created with proper tags for Node, Project, and Stack keys." if [ "${fail_empty_stacks}" = "true" ]; then echo "failing build" exit 1 fi else register_targets "$local_target_group_vpc" "$local_live_target_group_arn" "${local_staging_httpd_instance_prv_ips[@]}" wait_for_target_group_health "$local_live_target_group_arn" "HEALTHY" "${local_staging_httpd_instance_prv_ips[@]}" fi
# Demote Live to Staging (blue to green) echo "Demote Live to Staging (blue to green)" if [ -z "${local_live_httpd_instance_prv_ips}" ]; then echo "No live IP Addresses found when demoting to staging server." echo "Ensure nodes have been created with proper tags for Node, Project, and Stack keys." if [ "${fail_empty_stacks}" = "true" ]; then echo "failing build" exit 1 fi else register_targets "$local_target_group_vpc" "$local_staging_target_group_arn" "${local_live_httpd_instance_prv_ips[@]}" wait_for_target_group_health "$local_staging_target_group_arn" "HEALTHY" "${local_live_httpd_instance_prv_ips[@]}" fi
# Deregister previous Live from Live TG (remove blue from blue) echo "Deregister previous Live from Live TG (remove blue from blue)" if [ -z "${local_live_httpd_instance_prv_ips}" ]; then echo "No live IP Addresses found when deregistering from live TG." echo "Ensure nodes have been created with proper tags for Node, Project, and Stack keys and that it has a TGA." if [ "${fail_empty_stacks}" = "true" ]; then echo "failing build" exit 1 fi else deregister_targets "$local_target_group_vpc" "$local_live_target_group_arn" "${local_live_httpd_instance_prv_ips[@]}" # Wait for draining wait_for_target_group_health "$local_live_target_group_arn" "UNUSED" "${local_live_httpd_instance_prv_ips[@]}" fi
# Deregister previous Staging from Staging TG (remove green from green) echo "Deregister previous Staging from Staging TG (remove green from green)" if [ -z "${local_staging_httpd_instance_prv_ips}" ]; then echo "No staging IP Addresses found when deregistering from live TG." echo "Ensure nodes have been created with proper tags for Node, Project, and Stack keys and that it has a TGA." if [ "${fail_empty_stacks}" = "true" ]; then echo "failing build" exit 1 fi else deregister_targets "$local_target_group_vpc" "$local_staging_target_group_arn" "${local_staging_httpd_instance_prv_ips[@]}" # Wait for draining wait_for_target_group_health "$local_staging_target_group_arn" "UNUSED" "${local_staging_httpd_instance_prv_ips[@]}" fi echo "Swap Stacks complete" }' > target_group_attachment_functions.sh

cat <<'EOF' > pic_sure_rds_snapshot.sh # Use feature toggle to the strategy to use for picsure RDS # Available strategies # * STANDALONE = Unmanaged RDS instance. No prebuild blue snapshot # * STANDALONE_W_SS = Unmanaged RDS instance. With prebuild blue snapshot # * STANDALONE_W_TAGGED_SS = Unmanaged RDS instance. Will use a specified tagged snapshot to build from. # Tags are being tricky. But should work. # * MANAGED_EMPTY = Managed RDS instance. No prebuild blue snapshot # * MANAGED_USE_LATEST_SS = Managed RDS instance. Will take and use the latest snapshot of blue.
do_picsure_rds_strategy() { # variables local picsure_rds_strategy="${1}" local live_stack="${2}" local env_project="${3}"
# picsure_rds_standalone_instance= if [ -z ${picsure_rds_strategy} ]; then picsure_rds_snapshot_id= fi if [ "${picsure_rds_strategy}" == "STANDALONE" ]; then # placeholder. implement with persistent work echo "placeholder" fi if [ "${picsure_rds_strategy}" == "STANDALONE_W_SS" ]; then # placeholder. implement with persistent work echo "placeholder" fi if [ "${picsure_rds_strategy}" == "STANDALONE_W_TAGGED_SS" ]; then # placeholder. implement with persistent work echo "placeholder" fi # RDS Strategy: Managed RDS. Empty Database. if [ "${picsure_rds_strategy}" == "MANAGED_EMPTY" ]; then echo "initializing empty database." exit 0 fi # RDS Strategy: Managed RDS using latest SS of blue. if [ "${picsure_rds_strategy}" == "MANAGED_USE_LATEST_SS" ]; then echo "Creating snapshot for picsure rds" create_rds_snapshot_by_tag "${live_stack}" "${env_project}" picsure_rds_snapshot_id=$(get_latest_rds_snapshot_id "${picsure_rds_strategy}" "${live_stack}" "${env_project}") echo "$picsure_rds_snapshot_id" # validate snapshot if [ -z "$picsure_rds_snapshot_id" ]; then echo "Error: Error with RDS Snapshot. Snapshot empty. Ensure a snapshot exists for the blue rds instance." exit 1 fi # validate snapshot id format if [[ ! "$picsure_rds_snapshot_id" =~ ^[a-zA-Z0-9-]+$ ]]; then echo "Error: Error with RDS Snapshot. $picsure_rds_snapshot_id is not a valid snapshot id." exit 1 fi fi }
# Funtion to return latest snapshot for rds instance get_latest_rds_snapshot_id() { local picsure_rds_strategy="$1" local stack="$2" local project="$3"
if [ "${picsure_rds_strategy}" == "MANAGED_USE_LATEST_SS" ]; then db_instance_identifier=$(get_db_instance_identifier_by_tag "$live_stack" "$env_project") latest_snapshot=$(aws rds describe-db-snapshots \ --db-instance-identifier "$db_instance_identifier" \ --query "DBSnapshots | sort_by(@, &SnapshotCreateTime) | [-1].DBSnapshotIdentifier" \ --output text) fi echo "$latest_snapshot" }
# find db instance by stack and project tags get_db_instance_identifier_by_tag() { local stack="$1" local project="$2" local identifier identifier=$(aws rds describe-db-instances | jq --arg stack "$stack" --arg project "$project" -r '.DBInstances[] | select(.TagList[] | .Key == "Stack" and .Value == $stack) | select(.TagList[] | .Key == "Project" and .Value == $project) | .DBInstanceIdentifier') echo "$identifier" }
create_rds_snapshot_by_tag() { local live_stack="$1" local env_project="$2"
# Find the RDS instance based on the specified tag local db_instance_identifier db_instance_identifier=$(get_db_instance_identifier_by_tag "$live_stack" "$env_project")
if [ -z "$db_instance_identifier" ]; then echo "No RDS instance found with tag: $live_stack and $env_project" return 1 fi local timestamp=$(date +"%Y-%m-%d-%H-%M-%S") local snapshot_identifier="${db_instance_identifier}-snapshot-${timestamp}"
# Create a snapshot for the RDS instance aws rds create-db-snapshot \ --db-instance-identifier "$db_instance_identifier" \ --db-snapshot-identifier "$snapshot_identifier"
while true; do local status=$(aws rds describe-db-snapshots \ --db-instance-identifier "$db_instance_identifier" \ --db-snapshot-identifier "$snapshot_identifier" \ --query "DBSnapshots[0].Status" --output text)
if [ "${status,,}" = "available" ]; then echo "$snapshot_identifier" return 0 else sleep 20 fi done } EOF
