import jenkins.*
import jenkins.model.*
import hudson.*
import hudson.model.*
import groovy.json.JsonSlurper;

def buildSpec = new JsonSlurper().parse(new File("/var/jenkins_home/workspace/Check For Updates/build-spec.json"));

String s3_object_key ;
if(new File("/var/jenkins_home/workspace/Check For Updates/toBeRekeyed.txt").exists()){
    Jenkins.instance.getItemByFullName("Import_and_Rekey_HPDS_Data")
    .scheduleBuild2(0).get()
    s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/toBeRekeyed.txt").text
} else {
    s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/alreadyRekeyed.txt").text
}

def envVars= Hudson.instance.getItem("Check For Updates").lastBuild.properties.get("envVars"); 


Jenkins.instance.getItemByFullName("Deployment Pipeline")
    .scheduleBuild2(0, new ParametersAction([
        new StringParameterValue("deployment_git_hash", envVars["GIT_COMMIT"].trim()),
        new StringParameterValue("dataset_s3_object_key", s3_object_key.trim()),
])).get()
