import jenkins.*
import jenkins.model.*
import hudson.*
import hudson.model.*
import groovy.json.JsonSlurper;

def buildSpec = new JsonSlurper().parse(new File("/var/jenkins_home/workspace/Check For Updates/build-spec.json"));

def envVars= Hudson.instance.getItem("Check For Updates").lastBuild.properties.get("envVars");

String dataset_s3_object_key ;
if(new File("/var/jenkins_home/workspace/Check For Updates/toBeRekeyed.txt").exists()){
    def bucket_hash = new File("/var/jenkins_home/workspace/Check For Updates/toBeRekeyed.txt").text
    Jenkins.instance.getItemByFullName("Import_and_Rekey_HPDS_Data")
    .scheduleBuild2(0, new ParametersAction([
        new StringParameterValue("source_s3_url", buildSpec.source_dataset_s3_url.trim()),
        new StringParameterValue("output_file_name", "javabins_rekeyed"),
        new StringParameterValue("destination_bucket_hash", bucket_hash.trim()),
        new BooleanParameterValue("isDestigmatized",false)
        ])).get()
    dataset_s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/toBeRekeyed.txt").text
} else {
    dataset_s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/alreadyRekeyed.txt").text
}

String destigmatized_dataset_s3_object_key ;
if(new File("/var/jenkins_home/workspace/Check For Updates/destigmatized_toBeRekeyed.txt").exists()){
    def bucket_hash = new File("/var/jenkins_home/workspace/Check For Updates/destigmatized_toBeRekeyed.txt").text
    Jenkins.instance.getItemByFullName("Import_and_Rekey_HPDS_Data")
    .scheduleBuild2(0, new ParametersAction([
        new StringParameterValue("source_s3_url", buildSpec.source_destigmatized_dataset_s3_url.trim()),
        new StringParameterValue("output_file_name", "destigmatized_javabins_rekeyed"),
        new StringParameterValue("destination_bucket_hash", bucket_hash.trim()),
        new BooleanParameterValue("isDestigmatized",true)
        ])).get()
    destigmatized_dataset_s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/destigmatized_toBeRekeyed.txt").text
} else {
    destigmatized_dataset_s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/destigmatized_alreadyRekeyed.txt").text
}


genomic_s3_object_key = new File("/var/jenkins_home/workspace/Check For Updates/genomicDatasetKey.txt").text

Jenkins.instance.getItemByFullName("Deployment Pipeline")
    .scheduleBuild2(0, new ParametersAction([
        new StringParameterValue("deployment_git_hash", envVars["GIT_COMMIT"].trim()),
        new StringParameterValue("dataset_s3_object_key", dataset_s3_object_key.trim()),
        new StringParameterValue("destigmatized_dataset_s3_object_key", destigmatized_dataset_s3_object_key.trim()),
        new StringParameterValue("genomic_dataset_s3_object_key", genomic_s3_object_key.trim()),
])).get()
