definition.script:
import groovy.json.JsonSlurper;

def retrieveDepStateId
def stacksJson
def target_stack
def infrastructure_git_hash

pipeline {
    agent any
    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Deployment Pipeline/build-spec.json'))
                    println(buildSpec)
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('Acquire and lock deployment state') {
            steps {
                script {
                    def result = build job: 'Retrieve Deployment State'
                    retrieveDepStateId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Deployment State', selector: specific(""+retrieveDepStateId)
                    sh 'cat stacks.json'
                    stacksJson = new File('/var/jenkins_home/workspace/Deployment Pipeline/stacks.json').text
                }
            }
        }
        stage('Create Auth & Picsure Database Backups') {
            steps {
                parallel (
                    authSchema: {
                        script {
                            def authSchema = "auth"
                            def result = build job: 'Database Backup', parameters: [[$class: 'StringParameterValue', name: 'SOURCE_DATABASE', value: authSchema]]
                        }
                    },
                    picsureSchema: {
                        script {
                            def picsureSchema = "picsure"
                            def result = build job: 'Database Backup', parameters: [[$class: 'StringParameterValue', name: 'SOURCE_DATABASE', value: picsureSchema]]
                        }
                    }
                )
            }
        }
        stage('Database Migrations') {
            steps {
                script {
                    def result = build job: 'Database Migrations', parameters: [[$class: 'StringParameterValue', name: 'infrastructure_git_hash', value: infrastructure_git_hash]]
                }
            }
        }
        stage('Update PIC-SURE Token Introspection Token') {
            steps {
                script {
                    def result = build job: 'Update PIC-SURE Token Introspection Token'
                }
            }
        }
        stage('Teardown and Rebuild Stage Environment') {
            steps {
                script {
                    target_stack = new JsonSlurper().parseText(stacksJson).environments.next_prod
                    live_stack = new JsonSlurper().parseText(stacksJson).environments.prod
                    def result = build job: 'Teardown and Rebuild Stage Environment', parameters: [[$class: 'StringParameterValue', name: 'live_stack', value: live_stack], [$class: 'StringParameterValue', name: 'target_stack', value: target_stack], [$class: 'StringParameterValue', name: 'dataset_s3_object_key', value: env.dataset_s3_object_key], [$class: 'StringParameterValue', name: 'destigmatized_dataset_s3_object_key', value: env.destigmatized_dataset_s3_object_key], [$class: 'StringParameterValue', name: 'genomic_dataset_s3_object_key', value: env.genomic_dataset_s3_object_key], [$class: 'StringParameterValue', name: 'deployment_git_hash', value: env.deployment_git_hash], [$class: 'StringParameterValue', name: 'infrastructure_git_hash', value: infrastructure_git_hash]]
                }
            }
        }
        stage('Await Initialization') {
            steps {
                script {
                    def result = build job: 'Await Initialization', parameters: [[$class: 'StringParameterValue', name: 'target_stack', value: target_stack], [$class: 'StringParameterValue', name: 'git_hash', value: infrastructure_git_hash]]
                }
            }
        }
        stage('Update and unlock deployment state') {
            steps {
                script {
                    def result = build job: 'Write Stack State', parameters: [[$class: 'StringParameterValue', name: 'stacks_json', value: stacksJson]]
                }
            }
        }
    }
}
