    agent any
    parameters {
        string(name: 'TARGET_STACK', defaultValue: 'b', description: 'The stack you want to deploy to. a or b.')
    }
    stages {
        stage('PSAMA') {
            steps {
                script{
                    def result = build job: 'PIC-SURE Auth Micro-App Build - Jenkinsfile', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipeline_build_id],[$class: 'StringParameterValue', name: 'git_hash', value: PSAMA_GIT_HASH]]
                }
            }
        }
        stage('Update PSAMA on WildFly') {
            steps {
                script {
                    def result = build job: 'Deploy PSAMA to Existing WildFly EC2', parameters: [[$class: 'StringParameterValue', name: 'target_stack', value: TARGET_STACK],[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipeline_build_id]]
                }
            }
        }
    }
}
