definition.script:
pipeline { 
    agent any

    stages {
        stage('Nightly Task') {
            steps {

                script{
                    // If you would like to keep a stack you can change these values
                    def destroyGreen = true
                    def destroyBlue = true

                    // Triggering the job with boolean parameters
                    build job: 'Terraform Destroy Stack - Standalone', 
                          parameters: [booleanParam(name: 'destroy_green', value: destroyGreen),
                                       booleanParam(name: 'destroy_blue', value: destroyBlue)]
                }

            }
        }
    }
}
