definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash

pipeline {
    agent any
    parameters {
        booleanParam(name: 'RUN_DATABASE_MIGRATIONS', defaultValue: true, description: 'Run database migrations')
        booleanParam(name: 'DROP_AUTH_TABLES', defaultValue: false, description: 'Drop tables during migration')
        booleanParam(name: 'DROP_PICSURE_TABLES', defaultValue: false, description: 'Drop tables during migration')
        string(name: 'TARGET_STACK', defaultValue: 'b', description: 'The stack you want to deploy to a or b.')
        string(name: 'DATASET_S3_OBJECT_KEY', defaultValue: '', description: '')
        string(name: 'STACK_S3_BUCKET', defaultValue: '', description: '')
        string(name: 'ENABLE_DEBUG', defaultValue: 'false', description: 'Enable to setup remote debugging. Debugging will use port 9000.')
    }
    stages {
        stage('Retrieve Build Spec') {
            when {
                    expression { return params.BRANCH == '' }
            }
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Build and Deploy - PIC-SURE Auth Micro App/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/Build and Deploy - PIC-SURE Auth Micro App/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('PIC-SURE Auth Micro-App Build') {
            steps {
                script{
                    def result = build job: 'PIC-SURE Auth Micro-App Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSAMA']]]
                }
            }
        }
        stage('Database Migrations') {
            when {
                expression { return params.RUN_DATABASE_MIGRATIONS }
            }
            steps {
                script {
                    def result = build job: 'Database Migrations', parameters: [[$class: 'StringParameterValue', name: 'infrastructure_git_hash', value: infrastructure_git_hash],
                    [$class: 'BooleanParameterValue', name: 'DROP_PICSURE_TABLES', value: params.DROP_PICSURE_TABLES],[$class: 'BooleanParameterValue', name: 'DROP_AUTH_TABLES', value: params.DROP_AUTH_TABLES]]
                }
            }
        }
        stage('PIC-SURE Auth Micro App Deploy') {
            steps {
                script {
                    def result = build job: 'PIC-SURE Auth Micro App Deploy', parameters:
                        [
                            [$class: 'StringParameterValue', name: 'TARGET_STACK', value: params.TARGET_STACK],
                            [$class: 'StringParameterValue', name: 'DATASET_S3_OBJECT_KEY', value: params.DATASET_S3_OBJECT_KEY],
                            [$class: 'StringParameterValue', name: 'SPRING_PROFILE', value: params.SPRING_PROFILE],
                            [$class: 'StringParameterValue', name: 'ENABLE_DEBUG', value: params.ENABLE_DEBUG]
                        ]
                }
            }
        }
    }
}
