definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def targetStack;
pipeline {
    agent any
    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/PIC-SURE Pipeline/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/PIC-SURE Pipeline/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash

                        if(build.project_job_git_key == "OVERRIDE_UI") {
                            hasOverrideUI = true;
                                echo "Hex Code: ${build.project_job_git_key} = Color Name: ${build.git_hash}"
                        }
                    }
                }
            }
        }
        stage('Retrieve Stack') {
            steps {
                script {
                    targetStack = sh(script: """
                    bash -c '
                    set -x

                    if [ ! -d "$SOURCE_SCRIPTS_FOLDER" ]; then
                        echo "Error: Source folder does not exist: $SOURCE_SCRIPTS_FOLDER"
                        exit 1
                    fi

                    ls -la "$SOURCE_SCRIPTS_FOLDER"

                    for script_file in "$SOURCE_SCRIPTS_FOLDER"*.sh; do
                        chmod +x "\$script_file"
                        if [ -f "\$script_file" ] && [ -x "\$script_file" ]; then
                            echo "Sourcing \$script_file"
                            . "\$script_file"  # Use dot (.) instead of `source`
                        fi
                    done

                    # The PIC-SURE Pipeline is always used to deploy to staging.
                    get_stack \"${TARGET_STACK}\"
                    '
                    """, returnStdout: true).trim()

                    println "TARGET_STACK: $targetStack"
                }
            }
        }
        stage('PIC-SURE-API Build, PIC-SURE Frontend Build, PIC-SURE Auth Micro-App Build, PIC-SURE Dictionary Build'){
            steps {
                parallel (
                    picsureapi:{
                        script {
                            def result = build job: 'PIC-SURE API Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSA']]]
                        }
                    },
                    picsurefrontend:{
                        script{
                            def result = build job: 'PIC-SURE Frontend Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSF']]]
                        }
                    },
                    picsureauth:{
                        script{
                            def result = build job: 'PIC-SURE Auth Micro App Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSAMA']]]
                        }
                    },
                    dictionary:{
                        script {
                            def dictionaryBuildResult = build job: 'PIC-SURE Dictionary Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],[$class: 'StringParameterValue', name: 'GIT_BRANCH', value: build_hashes['PSD']]]
                        }
                    }
                )
            }
        }
        stage('PIC-SURE WildFly Build and PIC-SURE Dictionary Build'){
            steps {
                parallel (
                    wildfly:{
                        script {
                            def result = build job: 'PIC-SURE WildFly Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack]]
                        }
                    },
                    hpds:{
                        script {
                            def result = build job: 'PIC-SURE-HPDS Build', parameters: [[$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSH']]]
                        }
                    }
               )
            }
        }
    }
}
