definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash

pipeline {
    agent any
    parameters {
        string(name: 'TARGET_STACK', defaultValue: 'b', description: 'The stack you want to deploy to. a or b.')
        string(name: 'BRANCH', defaultValue: 'main', description: 'If you want to a specific branch. Leave blank to use the release control')
    }
    stages {
        stage('Retrieve Build Spec') {
            steps {
                when {
                    expression { return params.BRANCH == '' }
                }
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Deploy Dictionary in place/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/Deploy Dictionary in place/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('PIC-SURE Dictionary Build') {
            steps {
                script{
                    def gitHash = BRANCH ? BRANCH : build_hashes['PSD']

                    def dictionaryBuildResult = build job: 'PIC-SURE Dictionary Build', parameters: [
                        [$class: 'StringParameterValue', name: 'GIT_BRANCH', value: BRANCH]
                    ]
                }
            }
        }
        stage('PIC-SURE Dictionary Deploy') {
            steps {
                script {
                    def result = build job: 'PIC-SURE Dictionary Deploy', parameters: [
                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: params.TARGET_STACK]
                    ]
                }
            }
        }
    }
}
