definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash

pipeline {
    agent any
    parameters {
        string(name: 'TARGET_STACK', defaultValue: 'b', description: 'The stack you want to deploy to. a or b.')
        string(name: 'BRANCH', defaultValue: '', description: 'If you want to a specific branch. Leave blank to use the release control')
    }
    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Deploy New UI in place/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/Deploy New UI in place/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('Frontend') {
            steps {
                script{
                    def gitHash = BRANCH ? BRANCH : build_hashes['PSF']
                    def buildID = BRANCH ? BRANCH : pipelineBuildId
                    def result = build job: 'PIC-SURE Frontend Build', 
                        parameters: [
                            [$class: 'StringParameterValue', name: 'pipeline_build_id', value: buildID],
                            [$class: 'StringParameterValue', name: 'git_hash', value: gitHash]
                        ]
                }
            }
        }
        stage('Update Frontend on Httpd') {
            steps {
                script {
                    def buildID = BRANCH ? BRANCH : pipelineBuildId
                    def result = build job: 'PIC-SURE Frontend Deploy', parameters: [[$class: 'StringParameterValue', name: 'target_stack', value: TARGET_STACK],[$class: 'StringParameterValue', name: 'pipeline_build_id', value: buildID]]
                }
            }
        }
    }
}
