definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash;
def targetStack;

pipeline {
    agent any
    parameters {
        choice(name: 'TARGET_STACK', choices: ['staging','live'], description: 'Based on selection we determine if is a or b stack.')
        string(name: 'BRANCH', defaultValue: '', description: 'If you want to a specific branch. Leave blank to use the release control')
    }
    stages {
        stage('Retrieve Stack') {
            steps {
                script {
                    targetStack = sh(script: """
                    bash -c '
                    set -x

                    if [ ! -d "$SOURCE_SCRIPTS_FOLDER" ]; then
                        echo "Error: Source folder does not exist: $SOURCE_SCRIPTS_FOLDER"
                        exit 1
                    fi

                    ls -la "$SOURCE_SCRIPTS_FOLDER"

                    for script_file in "$SOURCE_SCRIPTS_FOLDER"*.sh; do
                        chmod +x "\$script_file"
                        if [ -f "\$script_file" ] && [ -x "\$script_file" ]; then
                            echo "Sourcing \$script_file"
                            . "\$script_file"  # Use dot (.) instead of `source`
                        fi
                    done

                    # The PIC-SURE Pipeline is always used to deploy to staging.
                    get_stack \"staging\"
                    '
                    """, returnStdout: true).trim()

                    println "TARGET_STACK: $targetStack"
                }
            }
        }
        stage('Retrieve Build Spec') {
            steps {
                when {
                    expression { return params.BRANCH == '' }
                }
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/PIC-SURE Frontend Build and Deploy/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/PIC-SURE Frontend Build and Deploy/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('Frontend') {
            steps {
                script{
                    def gitHash = BRANCH ? BRANCH : build_hashes['PSF']
                    def result = build job: 'PIC-SURE Frontend Build',
                        parameters: [
                            [$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack],
                            [$class: 'StringParameterValue', name: 'BRANCH', value: gitHash]
                        ]
                }
            }
        }
        stage('Update Frontend on Httpd') {
            steps {
                script {
                    def result = build job: 'PIC-SURE Frontend Deploy', parameters: [
                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: targetStack]
                    ]
                }
            }
        }
    }
}
