definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash

pipeline {
    agent any
    parameters {
        choice(name: 'TARGET_STACK', choices: ['staging','live'], description: 'Based on selection we determine if is a or b stack.')
        string(name: 'BRANCH', defaultValue: '', description: 'If you want to a specific branch. Leave blank to use the release control')
        string(name: 'STACK_S3_BUCKET', defaultValue: '${stack_s3_bucket}', description: 'If you want to a specific branch. Leave blank to use the release control')
        string(name: 'DESTIGMATIZED_DATASET_S3_OBJECT_KEY', defaultValue: '', description: 'Optional. Setting this value will update related files, folders, datasets, etc.')
    }
    stages {
        stage('Retrieve Build Spec') {
            steps {
                when {
                    expression { return params.BRANCH == '' }
                }
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/PIC-SURE HPDS Open Build and Deploy/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/PIC-SURE HPDS Open Build and Deploy/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('PIC-SURE HPDS Build') {
            steps {
                script {
                    def gitHash = BRANCH ? BRANCH : build_hashes['PSF']
                    def result = build job: 'PIC-SURE HPDS Build',
                        parameters: [
                            [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                            [$class: 'StringParameterValue', name: 'BRANCH', value: gitHash]
                        ]
                }
            }
        }
        stage('PIC-SURE HPDS Open Deploy') {
            steps {
                script {
                    def result = build job: 'PIC-SURE HPDS Open Deploy', parameters: [
                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                        [$class: 'StringParameterValue', name: 'STACK_S3_BUCKET', value: STACK_S3_BUCKET],
                        [$class: 'StringParameterValue', name: 'DESTIGMATIZED_DATASET_S3_OBJECT_KEY', value: DESTIGMATIZED_DATASET_S3_OBJECT_KEY],
                }
            }
        }
    }
}
