definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId = "";
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def targetStack;
pipeline {
    agent any
    environment {
        SOURCE_SCRIPTS_FOLDER = "${JENKINS_HOME}/workspace/Bash_Functions/"
    }

    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec', parameters: [[$class: 'StringParameterValue', name: 'RELEASE_CONTROL_BRANCH', value: RELEASE_CONTROL_BRANCH]]
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/PIC-SURE Pipeline Build and Deploy/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/PIC-SURE Pipeline Build and Deploy/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash

                        if(build.project_job_git_key == "OVERRIDE_UI") {
                            hasOverrideUI = true;
                                echo "Hex Code: ${build.project_job_git_key} = Color Name: ${build.git_hash}"
                        }
                    }
                }
            }
        }
        stage('PIC-SURE Build and Deploy'){
            steps {
                parallel (
                    picsureapi:{
                        script {
                            if (params.INCLUDE_PIC_SURE_API) {
                                def result = build job: 'PIC-SURE WildFly Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSA']]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE WildFly Build and Deploy"
                            }
                        }
                    },
                    picsurefrontend:{
                        script{
                            if (params.INCLUDE_PIC_SURE_FRONTEND) {
                                def result = build job: 'PIC-SURE Frontend Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'BRANCH', value: build_hashes['PSF']]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE Frontend Build and Deploy"
                            }
                        }
                    },
                    picsureauth:{
                        script{
                            if (params.INCLUDE_PIC_SURE_AUTH_MICRO_APP) {
                                def result = build job: 'PIC-SURE Auth Micro App Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'BRANCH', value: build_hashes['PSAMA']],
                                        [$class: 'StringParameterValue', name: 'DATASET_S3_OBJECT_KEY', value: params.DATASET_S3_OBJECT_KEY],
                                        [$class: 'BooleanParameterValue', name: 'RUN_DATABASE_MIGRATIONS', value: false]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE Auth Micro App Build and Deploy"
                            }
                        }
                    },
                    dictionary:{
                        script {
                            if (params.INCLUDE_PIC_SURE_DICTIONARY) {
                                def dictionaryBuildResult = build job: 'PIC-SURE Dictionary Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'GIT_HASH', value: build_hashes['PSD']]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE Dictionary Build and Deploy"
                            }
                        }
                    },
                    authhpds:{
                        script {
                            if (params.INCLUDE_PIC_SURE_HPDS_AUTH) {
                                def dictionaryBuildResult = build job: 'PIC-SURE HPDS Auth Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSD']],
                                        [$class: 'StringParameterValue', name: 'DATA_S3_OBJECT_KEY', value: DATA_S3_OBJECT_KEY],
                                        [$class: 'StringParameterValue', name: 'GENOMIC_DATASET_S3_OBJECT_KEY', value: GENOMIC_DATASET_S3_OBJECT_KEY]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE HPDS Auth Build and Deploy"
                            }
                        }
                    },
                    openhpds:{
                        script {
                            if (params.INCLUDE_PIC_SURE_HPDS_OPEN) {
                                def dictionaryBuildResult = build job: 'PIC-SURE HPDS Open Build and Deploy', parameters: [
                                        [$class: 'StringParameterValue', name: 'TARGET_STACK', value: TARGET_STACK],
                                        [$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSD']],
                                        [$class: 'StringParameterValue', name: 'DESTIGMATIZED_DATASET_S3_OBJECT_KEY', value: DESTIGMATIZED_DATASET_S3_OBJECT_KEY]
                                    ]
                            } else {
                                echo "Skipping PIC-SURE HPDS Open Build and Deploy"
                            }
                        }
                    }
                )
            }
        }
    }
}
