definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
pipeline {
    agent any
    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/PIC-SURE Pipeline/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/PIC-SURE Pipeline/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash

                        if(build.project_job_git_key == "OVERRIDE_UI") {
                            hasOverrideUI = true;
                                echo "Hex Code: ${build.project_job_git_key} = Color Name: ${build.git_hash}"
                        }
                    }
                }
            }
        }
        stage('PIC-SURE API, PIC-SURE-HPDS UI') {
            steps {
                parallel (
                    picsureapi:{
                        script {
                            def result = build job: 'PIC-SURE-API Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSA']]]
                        }
                    },
                    picsurefrontend:{
                        script{
                            def result = build job: 'PIC-SURE Frontend Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSF']]]
                        }
                    }
                )
            }
        }
        stage('PSAMA and PIC-SURE HPDS'){
            steps {
                parallel (
                    picsurehpds:{
                        script {
                            def result = build job: 'PIC-SURE-HPDS Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSH']]]
                        }
                    },
                    picsureauth:{
                        script{
                            def result = build job: 'PIC-SURE Auth Micro-App Build - Jenkinsfile', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSAMA']]]
                        }
                    }
               )
            }
        }
        stage('PIC-SURE Dictionary image '){
            steps {
                script {
                    # PSS Picsure search prototype
                    build job: 'PIC-SURE-HPDS-DICTIONARY Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSS']]]
                    build job: 'PIC-SURE-HPDS-DICTIONARY-RESOURCE Build', parameters: [[$class: 'StringParameterValue', name: 'destination_bucket_hash', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSS']]]

                    # PSD Picsure Dictionary
                    def dictionaryBuildResult = build job: 'PIC-SURE Dictionary - Jenkinsfile', parameters: [
                        [$class: 'StringParameterValue', name: 'GIT_BRANCH', value: build_hashes['PSD']]
                    ]
                }
            }
        }
        stage('Wildfly Build and BDC UI'){
            steps {
                parallel (
                    wildfly:{
                        script {
                            def result = build job: 'PIC-SURE Wildfly Image Build', parameters: [[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId]]
                        }
                    }
               )
            }
        }
    }
}
