definition.script:
import groovy.json.JsonSlurper;
def bdc_ui_docker_tag;

def retrieveBuildSpecId;
def bdcUIBuildSpecId;
def overrideUiBuildSpec;
def pipelineBuildId;
def build_hashes = {};
def hasOverrideUI;
def infrastructure_git_hash

pipeline {
    agent any
    parameters {
        booleanParam(name: 'RUN_DATABASE_MIGRATIONS', defaultValue: true, description: 'Run database migrations')
        booleanParam(name: 'DROP_AUTH_TABLES', defaultValue: false, description: 'Drop tables during migration')
        booleanParam(name: 'DROP_PICSURE_TABLES', defaultValue: false, description: 'Drop tables during migration')
        string(name: 'TARGET_STACK', defaultValue: 'b', description: 'The stack you want to deploy to. a or b.')
    }
    stages {
        stage('Retrieve Build Spec') {
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Production Fix - Privileges/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/Production Fix - Privileges/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                    infrastructure_git_hash = buildSpec.infrastructure_git_hash
                }
            }
        }
        stage('Update Bash Functions') {
            steps {
                script {
                    def result = build job: 'Bash_Functions'
                }
            }
        }
        stage('Database - Dump') {
            steps {
                script {
                    def result = build job: 'Database - Copy', parameters: 
                    [[$class: 'StringParameterValue', name: 'SOURCE_DATABASE', value: "auth"]]
                }
            }
        }
        stage('Database - Copy') {
            steps {
                script {
                    def result = build job: 'Database - Copy', parameters: 
                    [[$class: 'StringParameterValue', name: 'SOURCE_DATABASE', value: "auth"],
                    [$class: 'StringParameterValue', name: 'TARGET_DATABASE', value: "auth2"]]
                }
            }
        }
        stage('Database Migrations') {
            when {
                expression { return params.RUN_DATABASE_MIGRATIONS }
            }
            steps {
                script {
                    def result = build job: 'Database Migrations', parameters: [
                        [$class: 'StringParameterValue', name: 'infrastructure_git_hash', value: infrastructure_git_hash],
                        [$class: 'BooleanParameterValue', name: 'DROP_PICSURE_TABLES', value: false],
                        [$class: 'BooleanParameterValue', name: 'DROP_AUTH_TABLES', value: params.DROP_AUTH_TABLES],
                        [$class: 'StringParameterValue', name: 'COPY_APPLICATION_TOKEN_FROM_DB', value: "auth2"]]
                }
            }
        }
        stage('Update PSAMA on WildFly') {
            steps {
                script {
                    def result = build job: 'Deploy PSAMA to Existing WildFly EC2 - Standalone Tomcat Container', parameters: [[$class: 'StringParameterValue', name: 'target_stack', value: TARGET_STACK],[$class: 'StringParameterValue', name: 'pipeline_build_id', value: pipelineBuildId]]
                }
            }
        }
    }
}
