hudson.tasks.Shell.command:
# json tag schema.  Used to uniquely identify a projects staging instances.
cat <<EOF > staging_httpd_tags_file.json
[
  {
    "Name": "tag:Stack",
    "Values": ["$target_stack"]
  },
  {
    "Name": "tag:Node",
    "Values": ["HTTPD"]
  },
  {
    "Name": "tag:Project",
    "Values": ["$env_project"]
  }
]
EOF
