hudson.tasks.Shell.command:
cd app-infrastructure
wget https://releases.hashicorp.com/terraform/0.12.31/terraform_0.12.31_linux_amd64.zip && unzip terraform_0.12.31_linux_amd64.zip && mv terraform /usr/local/bin/

aws sts assume-role --duration-seconds 900 --role-arn "arn:aws:iam::${cnc_acct_id}:role/system/jenkins-s3-role" --role-session-name "teardown-rebuild" > assume-role-output.txt

export AWS_ACCESS_KEY_ID=`grep AccessKeyId assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`
export AWS_SECRET_ACCESS_KEY=`grep SecretAccessKey assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`
export AWS_SESSION_TOKEN=`grep SessionToken assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`

aws s3 cp s3://$stack_s3_bucket/deployment_state_metadata/${target_stack}/terraform.tfstate .
aws s3 cp s3://$stack_s3_bucket/deployment_state_metadata/${target_stack}/stack_variables.tf .
aws s3 cp s3://$stack_s3_bucket/deployment_state_metadata/${target_stack}/subnet_variables.tf .
cp stack_variables.tf ../s3-deployment-roles/
cp subnet_variables.tf ../s3-deployment-roles/
aws s3 cp s3://$stack_s3_bucket/deployment_state_metadata/${target_stack}/terraform.tfstate_roles ../s3-deployment-roles/terraform.tfstate || echo "role state doesnt exist, it will be created"

unset AWS_ACCESS_KEY_ID
unset AWS_SECRET_ACCESS_KEY
unset AWS_SESSION_TOKEN

aws sts assume-role --duration-seconds 900 --role-arn "arn:aws:iam::${app_acct_id}:role/hms-dbmi-cnc-role" --role-session-name "teardown-rebuild" > assume-role-output.txt

export AWS_ACCESS_KEY_ID=`grep AccessKeyId assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`
export AWS_SECRET_ACCESS_KEY=`grep SecretAccessKey assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`
export AWS_SESSION_TOKEN=`grep SessionToken assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g"`

cd ../s3-deployment-roles
terraform init
terraform destroy -auto-approve -var="dataset-s3-object-key=${dataset_s3_object_key}" -var="destigmatized-dataset-s3-object-key=${destigmatized_dataset_s3_object_key}" -var="genomic-dataset-s3-object-key=${genomic_dataset_s3_object_key}"  -var="target-stack=${target_stack}" -var="stack_githash=`echo ${deployment_git_hash} |cut -c1-7`" -var="stack_githash_long=${deployment_git_hash}" || true
terraform apply -auto-approve -var="dataset-s3-object-key=${dataset_s3_object_key}"  -var="destigmatized-dataset-s3-object-key=${destigmatized_dataset_s3_object_key}" -var="genomic-dataset-s3-object-key=${genomic_dataset_s3_object_key}" -var="target-stack=${target_stack}" -var="stack_githash=`echo ${deployment_git_hash} |cut -c1-7`" -var="stack_githash_long=${deployment_git_hash}" || true

DOMAINS="${allowed_hosts}"
# Convert commas (with optional whitespace) to spaces
DOMAINS_ARRAY=$(echo "$DOMAINS" | sed 's/,\s*/ /g')
# Convert array of domains to regex
REGEX_DOMAINS=$(echo "$DOMAINS_ARRAY" | sed 's/ /|/g')

cd ../app-infrastructure

#uncomment for dev systems
#sed -i -r 's/(\$\{target-stack\})/\1.dev/' configs/* scripts/*

terraform init

terraform destroy -auto-approve -var="ami-id=${ami_id}" -var="dataset-s3-object-key=${dataset_s3_object_key}"  -var="destigmatized-dataset-s3-object-key=${destigmatized_dataset_s3_object_key}" -var="genomic-dataset-s3-object-key=${genomic_dataset_s3_object_key}" -var="target-stack=${target_stack}" -var="stack_githash=`echo ${deployment_git_hash} |cut -c1-7`" -var="stack_githash_long=${deployment_git_hash}" -var="allowed_hosts=${REGEX_DOMAINS}" || true
terraform apply -auto-approve -var="ami-id=${ami_id}" -var="dataset-s3-object-key=${dataset_s3_object_key}"  -var="destigmatized-dataset-s3-object-key=${destigmatized_dataset_s3_object_key}" -var="genomic-dataset-s3-object-key=${genomic_dataset_s3_object_key}" -var="target-stack=${target_stack}" -var="stack_githash=`echo ${deployment_git_hash} |cut -c1-7`" -var="stack_githash_long=${deployment_git_hash}" -var="allowed_hosts=${REGEX_DOMAINS}" || true

echo "
{
  \"Changes\": [
    {
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"httpd.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"A\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep httpd-ec2-private_ip ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    },{
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"wildfly.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"A\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep wildfly-ec2-private_ip ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    },{
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"open-hpds.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"A\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep open-hpds-ec2-private_ip ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    },{
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"auth-hpds.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"A\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep auth-hpds-ec2-private_ip ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    },{
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"dictionary.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"A\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep dictionary-ec2-private_ip ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    },{
      \"Action\": \"UPSERT\",
      \"ResourceRecordSet\": {
        \"Name\": \"picsure-db.${target_stack}.datastage.hms.harvard.edu\",
        \"Type\": \"CNAME\",
        \"TTL\": 60,
        \"ResourceRecords\": [
          {
            \"Value\": \"$(grep pic-sure-mysql-address ip-vars.properties | cut -d "=" -f 2)\"
          }
        ]
      }
    }
