hudson.tasks.Shell.command:
#!/bin/bash

echo "aws s3 cp ${jenkins_config_s3_location} ${jenkins_config_s3_location}_backup"
echo $(env)
aws s3 cp "${jenkins_config_s3_location}" "${jenkins_config_s3_location}_backup"

echo "aws s3 cp ${JENKINS_HOME}/config.xml ${jenkins_config_s3_location}"
aws s3 cp ${JENKINS_HOME}/config.xml ${jenkins_config_s3_location}
