hudson.tasks.Shell.command:
#!/bin/bash set -e # Source folder containing the scripts source_scripts_folder="${JENKINS_HOME}/workspace/Bash_Functions/" ls -la "$source_scripts_folder"
# Iterate through the files in the folder and source them for script_file in "$source_scripts_folder"*.sh; do chmod +x "$script_file" if [ -f "$script_file" ] && [ -x "$script_file" ]; then echo "sourcing $script_file" source "$script_file" fi done
create_jwt_token $picsure_client_secret "$pic_sure_introspection_token_repo" "PSAMA_APPLICATION|$application_id_for_base_query" "${duration_int:-365}" "${duration_unit:-day}" if [ -n ${CREATE_JWT_TOKEN_OUTPUT} ]; then echo "New jwt generated update successfully." echo "Update the picsure_introspection_token configuration with the following:" echo "${CREATE_JWT_TOKEN_OUTPUT}" echo "${CREATE_JWT_TOKEN_OUTPUT}" > new_token else echo "Token was not generated successfully check that picsure_client_secret, pic_sure_introspection_token_repo, application_id_for_base_query configurations are a valid value." exit 1 fi
