script.script:
import groovy.json.*
import java.io.*

buildSubnetVariablesFileForStack("a");
buildSubnetVariablesFileForStack("b");

void buildSubnetVariablesFileForStack(String stackId){
def JsonSlurper js = new JsonSlurper()

def subnetsResponse = js.parse(new File("/var/jenkins_home/workspace/Update VPC Settings/subnets_${stackId}.json"))

def subnetNames = [
  "edge-subnet-us-east-1a",
  "edge-subnet-us-east-1b",
  "app-subnet-us-east-1a",
  "app-subnet-us-east-1b",
  "db-subnet-us-east-1a",
  "db-subnet-us-east-1b",
]

def subnetAWSNames = [
  "edge-subnet-us-east-1a":"Web-Subnet-AZ1",
  "edge-subnet-us-east-1b":"Web-Subnet-AZ2",
  "app-subnet-us-east-1a":"App-Subnet-AZ1",
  "app-subnet-us-east-1b":"App-Subnet-AZ2",
  "db-subnet-us-east-1a":"Database-Subnet-AZ1",
  "db-subnet-us-east-1b":"Database-Subnet-AZ2",
]

def subnetVars = [:]
String subnetVariables = ""

for(def subnetName : subnetNames){
  def subnet = getSubnetForName(subnetAWSNames.get(subnetName), subnetsResponse);
  subnetVariables+="variable \"" + subnetName + "-id\" {\n";
  subnetVariables+="    type = string\n";
  subnetVariables+="    default = \""+ subnet.SubnetId +"\"\n";
  subnetVariables+="}\n";
  subnetVariables+="variable \"" + subnetName + "-cidr\" {\n";
  subnetVariables+="    type = string\n";
  subnetVariables+="    default = \""+ subnet.CidrBlock +"\"\n";
  subnetVariables+="}\n";
}
def zoneId = build.buildVariableResolver.resolve("R53_Zone_ID")
subnetVariables+="""
variable "internal-dns-zone-id" {
  type = string
  default = "$zoneId"
}
"""
def vpcId = build.buildVariableResolver.resolve("vpc_$stackId")
subnetVariables+="""
variable "target-vpc" {
  type = string
  default = "$vpcId"
}
"""
def subnetGroupName = build.buildVariableResolver.resolve("db-subnet-group-name-$stackId")
subnetVariables+="""
variable "db-subnet-group-name" {
  type = string
  default = "$subnetGroupName"
}
"""

println(subnetVariables)

new File("/var/jenkins_home/workspace/Update VPC Settings/subnet_variables_${stackId}.tf").write(subnetVariables)
}


def getSubnetForName(String name, def subnetsResponse){
  return subnetsResponse.Subnets.find(
    {it->it.Tags.find(
      {tag->return tag.Key.contentEquals("Name") && tag.Value.contentEquals(name)}
    )}
  );
}
